import {useParams} from "react-router-dom";
import {useEffect, useState} from "react";

const ReportDetails = () => {
    const {reportID} = useParams();

    const [report, setReport] = useState({});

    const deleteReport = async (e) => {
        e.preventDefault()

        console.log('deleted')
    }

    useEffect(() => {
            fetch(`/api/report/view/${reportID}`, {
                credentials: 'include',
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json'
                }
            })
                .then(res => res.json())
                .then(res => setReport(res))
    }, [])


    return <>
        <h1 className='text-center'>Report Details for {reportID}</h1>
        <br/>
        <div className='list-group list-group-flush card'>
            <h5 className='text-center ist-group-item card-text'>{report._id}</h5>
            <h5 className='text-center ist-group-item card-text'>{report.blogID}</h5>
            <p className='text-center ist-group-item card-text text-area'>{report.reportDescription}</p>
        </div>

        <br />
        <button className='btn btn-danger' onClick={deleteReport} >
            Delete
        </button>
    </>
}

export default ReportDetails;
