import {useSignIn} from "react-auth-kit";
import React, {useEffect, useState} from "react";
import {useNavigate, useParams} from "react-router-dom";

function Login(props: any) {
    let msg = useParams();
    const signIn = useSignIn();
    const [token, setToken] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const nav = useNavigate();

    const submit = async (e) => {
        e.preventDefault();

        const login = {username, password}

        const res = await fetch('/api/user/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(login)
        })

        try {
            const json = await res.json();
            setToken(json)
        }
        catch (err){
            console.log(err)
            nav('/login?message=401')
        }
    }

    useEffect(() => {
        console.log(token)
        if(token) {
            signIn({
                token: token.token,
                expiresIn: 60,
                tokenType: 'Bearer',
                authState: {username: username}
            })

            nav('/')
        }
    }, [token])


    return <>
        <div className='card'>
            <h2 className='text-center card-title'>Login</h2>
            <form className='form card-body' onSubmit={submit} id='login-form'>
                <div className='row'>
                    <div className='col-md-2'></div>
                    <fieldset className='col-md-8'>
                        <label htmlFor='email'>Username/Email</label>
                        <input
                            className='form-control'
                            value={username}
                            onChange={(e) => setUsername(e.target.value) }
                            type='text'
                            name='username'
                            id='username' />
                    </fieldset>
                </div>
                <br />
                <div className='row'>
                    <div className='col-md-2'></div>
                    <fieldset className='col-md-8'>
                        <label htmlFor='password'>Password</label>
                        <input
                            className='form-control'
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            type='password'
                            name='password'
                            id='password' />
                    </fieldset>
                </div>
                <br />
                <div className='row'>
                    <div className='col-md-6'></div>
                    <div className='col-md-4'>
                        <button className='btn btn-primary text-center' type='submit' id='login'>
                            Login
                        </button>
                    </div>
                </div>
                { msg.message === 401 ?? <span className='span btn btn-danger text-center'>
                    Incorrect login credentials
                </span> }
            </form>
        </div>
    </>
}

export default Login;
