import {useSignIn} from "react-auth-kit";
import React, {useEffect, useState} from "react";
import {json, useNavigate} from "react-router-dom";

function Login(props: any) {
    const signIn = useSignIn();
    const [token, setToken] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const nav = useNavigate();

    const submit = (e) => {
        e.preventDefault();

        const login = {username, password}

        const res = fetch('/api/user/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(login)
        }).then(resp => {
            if(resp.status === 400) {
                nav('/login')
            }
            else {
                resp.json().then(json => {setToken(json)})
            }
        })

        console.log(token)


        signIn({
            token: token.token,
            expiresIn: 60,
            tokenType: 'Bearer',
            authState: {username: username}
        })
    }


    return <>
        <div className='card'>
            <h2 className='text-center card-title'>Login</h2>
            <form className='form card-body' onSubmit={submit} id='login-form'>
                <div className='row'>
                    <div className='col-md-2'></div>
                    <fieldset className='col-md-8'>
                        <label htmlFor='email'>Username/Email</label>
                        <input
                            className='form-control'
                            value={username}
                            onChange={(e) => setUsername(e.target.value) }
                            type='text'
                            name='username'
                            id='username' />
                    </fieldset>
                </div>
                <br />
                <div className='row'>
                    <div className='col-md-2'></div>
                    <fieldset className='col-md-8'>
                        <label htmlFor='password'>Password</label>
                        <input
                            className='form-control'
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            type='password'
                            name='password'
                            id='password' />
                    </fieldset>
                </div>
                <br />
                <div className='row'>
                    <div className='col-md-6'></div>
                    <div className='col-md-4'>
                        <button className='btn btn-primary text-center' type='submit' id='login'>
                            Login
                        </button>
                    </div>
                </div>
            </form>
        </div>
    </>
}

export default Login;
