import {useSignIn} from "react-auth-kit";

function Login(props: any) {
    const signIn = useSignIn();
    const submit = async (values: any) => {
        values.preventDefault()
        console.log(values)

        const post = await fetch('/api/user/login', {
            method: 'POST',
            body: values,
            headers: {
                "Content-Type": "application/x-www-form-urlencoded"
            }
        })

        try {
            const token = await post.json;

            console.log(JSON.stringify(token))

            signIn({
                token: token,
                expiresIn: 3600,
                tokenType: 'Bearer',
                authState: {username: values.username}
            })
        }
        catch (err) {
            console.log(err)
        }
    }

    return <>
        <div className='card'>
            <h2 className='text-center card-title'>Login</h2>
            <form className='form card-body' onSubmit={submit}>
                <div className='row'>
                    <div className='col-md-2'></div>
                    <fieldset className='col-md-8'>
                        <label htmlFor='email'>Username/Email</label>
                        <input className='form-control' type='text' name='username' id='username' />
                    </fieldset>
                </div>
                <br />
                <div className='row'>
                    <div className='col-md-2'></div>
                    <fieldset className='col-md-8'>
                        <label htmlFor='password'>Password</label>
                        <input className='form-control' type='password' name='password' id='password' />
                    </fieldset>
                </div>
                <br />
                <div className='row'>
                    <div className='col-md-6'></div>
                    <div className='col-md-4'>
                        <button className='btn btn-primary text-center' type='submit'>
                            Login
                        </button>
                    </div>
                </div>
            </form>
        </div>
    </>
}

export default Login;
